section.t:
The CA MUST validate the signature on the certificate request using the
public key from the signature certificate.  If the signature is not valid,
then the CA MUST reject the certificate request.

section.sourcecode:
id-at-statementOfPossession OBJECT IDENTIFIER ::=
  { 1 3 6 1 4 1 22112 2 1 }

privateKeyPossessionStatement ATTRIBUTE ::= {
  TYPE PrivateKeyPossessionStatement
  IDENTIFIED BY id-at-statementOfPossession }

PrivateKeyPossessionStatement ::= SEQUENCE {
  signer  IssuerAndSerialNumber,
  cert    Certificate OPTIONAL }

PrivateKeyPossessionStatement-2025 { iso(1) identified-organization(3) dod(6) internet(1) security(5) mechanisms(5) pkix(7) id-mod(0) id-mod-private-key-possession-stmt-2025(118) }
DEFINITIONS IMPLICIT TAGS ::= BEGIN
EXPORTS ALL;
IMPORTS ATTRIBUTE FROM PKIX-CommonTypes-2009 -- in [RFC5912] { iso(1) identified-organization(3) dod(6) internet(1) security(5) mechanisms(5) pkix(7) id-mod(0) id-mod-pkixCommon-02(57) }
Certificate FROM PKIX1Explicit-2009 -- in [RFC5912] { iso(1) identified-organization(3) dod(6) internet(1) security(5) mechanisms(5) pkix(7) id-mod(0) id-mod-pkix1-explicit-02(51) }
IssuerAndSerialNumber FROM CryptographicMessageSyntax-2010 -- [RFC6268] { iso(1) member-body(2) us(840) rsadsi(113549) pkcs(1) pkcs-9(9) smime(16) modules(0) id-mod-cms-2009(58) } ;
-- -- Private Key Possession Statement Attribute --
id-at-statementOfPossession OBJECT IDENTIFIER ::= { 1 3 6 1 4 1 22112 2 1 }
privateKeyPossessionStatement ATTRIBUTE ::= { TYPE PrivateKeyPossessionStatement IDENTIFIED BY id-at-statementOfPossession }
PrivateKeyPossessionStatement ::= SEQUENCE { signer IssuerAndSerialNumber, cert Certificate OPTIONAL }
-- -- Registration Control Support --
RegControlSet ATTRIBUTE ::= { regCtrl-privateKeyPossessionStatement, ... }
regCtrl-privateKeyPossessionStatement ATTRIBUTE ::= privateKeyPossessionStatement
id-regCtrl-statementOfPossession OBJECT IDENTIFIER ::= id-at-statementOfPossession
END

